import React, { useState } from 'react';

const CityData = () => {
    const [newYorkData, setNewYorkData] = useState([])
    let fetchDataOnButtonClick = false; // Example condition
    let number = 0;

    function fetchDataAndUpdate() {
      const fetchData = async () => {
        try {
          const response = await fetch('http://localhost:3001/new-york-data');
          if (!response.ok) {
            throw new Error('Failed to fetch data');
          }
          const data = await response.json();
          setNewYorkData(data)
          console.log('Fetched data:', data); 
          number += 1;
        } catch (error) {
          console.error('Error fetching data:', error);
        }
      };

      if (fetchDataOnButtonClick) {
        fetchData(); // Initial fetch
        const intervalId = setInterval(fetchData, 10000); // Set interval to fetch data every 10 seconds
        return () => clearInterval(intervalId); // Cleanup function to clear interval on unmount or condition change
      }
    }

    // Example condition change to simulate useEffect behavior
    fetchDataOnButtonClick = true; // Set to true to start fetching data and set interval

    // Initial call to fetchDataAndUpdate function
    fetchDataAndUpdate();


  return (
    <div>
      <h1>New York City Attractions</h1>
      {fetchDataOnButtonClick && newYorkData[number]}
    </div>
  );
};

export default CityData;
